package com.example;

import com.beust.jcommander.JCommander;
import com.beust.jcommander.Parameter;
import com.beust.jcommander.Parameters;

public class App {
    static class Argument {
        @Parameter(names = { "-v", "--version" }, description = "app version")
        Boolean isVersion;

        @Parameter(names = { "-h", "--help" }, description = "How to usage")
        Boolean isHelp = false;
    }

    @Parameters(commandNames = "init", commandDescription = "app Initialization")
    static class InitArgument {
        @Parameter(names = { "-s", "--space_key" }, description = "your space key")
        String space_key;

        @Parameter(names = { "--client_id" }, description = "your client id")
        String client_id;

        @Parameter(names = { "--client_secret" }, description = "your client secret")
        String client_secret;
    }

    @Parameters(commandNames = "list", commandDescription = "show issue list")
    static class ListArgument {
        @Parameter(names = { "-a", "--all" }, description = "Issues for all users")
        Boolean isFetchAllTasks = false;
    }

    @Parameters(commandNames = "view", commandDescription = "show issue detail")
    static class ViewArgument {
        @Parameter(names = { "--id" }, description = "issue id", required = true)
        long issueId;

        @Parameter(names = { "-c", "--comments" }, description = "get comments")
        Boolean hasComments = false;
    }

    @Parameters(commandNames = "update", commandDescription = "update issue status")
    static class UpdateArgument {
        @Parameter(names = { "--id" }, description = "issue id", required = true)
        String issueId;
    }

    public static void main(String... args) {
        var argument = new Argument();
        var initArgument = new InitArgument();
        var listArgument = new ListArgument();
        var viewArgument = new ViewArgument();
        var updateArgument = new UpdateArgument();

        var commander = JCommander.newBuilder()
                .programName("appName")
                .addObject(argument)
                .addCommand(initArgument)
                .addCommand(listArgument)
                .addCommand(viewArgument)
                .addCommand(updateArgument)
                .build();

        // コマンドラインパラメーター解析
        commander.parse(args);

        // 使用方法（パラメーターの種類）表示
        if (argument.isHelp) {
            commander.usage();
            return;
        }

        String command = commander.getParsedCommand();
        switch (command) {
            case "init":
                System.out.println("[WIP] subcommand: init ToDo: OAuth認証");
                Init initCmd = new Init();
                initCmd.exec();
                break;
            case "list":
                // サブコマンドのオプション取得
                Boolean isFetchAllTasks = listArgument.isFetchAllTasks;
                IssueList issueListCmd = new IssueList();
                issueListCmd.exec(isFetchAllTasks);
                break;
            case "view":
                System.out.println("[WIP] subcommand: view");
                // サブコマンドのオプション取得
                long issueId = viewArgument.issueId;
                Boolean hasComments = viewArgument.hasComments;

                IssueView viewCmd = new IssueView();
                viewCmd.exec(issueId, hasComments);
                break;
            case "update":
                System.out.println("[ToDo] subcommand: update");
                break;
            default:
                commander.usage();
        }
    }
}
